class scrollToTop extends HTMLElement{connectedCallback(){this.innerHTML+='\n    <a href="#" class="to-top" aria-label="Scroll to top" title="Scroll to top">\n      <svg width="50px" height="50px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">\n        <circle cx="12" cy="12" r="11.5" fill="none" stroke="#fff" stroke-width="1"/>\n        <path d="M6 15L12 9L18 15" stroke="#fff" stroke-width="1" stroke-linecap="round" stroke-linejoin="round"/>\n      </svg>\n    </a>\n    '}}customElements.define("to-top",scrollToTop),document.querySelector("body").appendChild(document.createElement("to-top"));const toTop=document.querySelector("to-top");window.addEventListener("scroll",(()=>{window.pageYOffset>100?toTop.classList.add("active"):toTop.classList.remove("active")})),footerContainer=document.querySelector("footer");const year=(new Date).getFullYear();function footerContainerF(e){e&&(e.innerHTML+=`\n      <p>Copyright © <a href="https://primesolar.github.io/web-developer/" rel="noopener noreferrer">Vladislav Kazantsev</a> ${year}</p>\n      <a type="button" class="bmc-button" href="https://www.buymeacoffee.com/CocaCola" target="_blank" rel="noopener noreferrer" aria-label="Buy me a coffee" role="button">☕ Buy me a coffee</a>`)}footerContainerF(footerContainer);
